import React from 'react'
import { createBrowserRouter, RouterProvider } from 'react-router-dom'

import MainPage from './routes/MainPage'
import MyWork from './routes/MyWork'
import Contact from './routes/Contact'
import AboutMe from './routes/AboutMe'


const router = createBrowserRouter([
  { path: "/", element: < MainPage loadingAnim={this.state.loadingAnim} /> },
  { path: "/myWork", element: < MyWork loadingAnim={this.state.loadingAnim} /> },
  { path: "/contact", element: < Contact loadingAnim={this.state.loadingAnim} /> },
  { path: "/aboutMe", element: < AboutMe loadingAnim={this.state.loadingAnim} /> }
]
)

class App extends React.Component {
  constructor() {
    super()
    this.state = {
      loading: false
    }
  }

  loadingAnim() {
    this.setState({ loading: true })
    setTimeout(() => {
      this.setState({ loading: false })

    }, 3000)
  }

  render() {
    return (
      < RouterProvider router={router} />
    )
  }
}

export default App
